  encoder: function(val, bufb) {
    var code = 0x80;
    if (val instanceof Int64 || val > 0xFF) {
      var check = (val instanceof Int64) ? val.toNumber(true) : val;
      if (check === 0) {
        code = 0x44;
      } else if (check <= 0xFF) {
        code = 0x53;
      }
