  if (!this._socket) {
    throw new errors.TransportError('Socket not connected');
  }

  this._socket.end();
};

TlsTransport.prototype.destroy = function() {
  if (this._socket) {
    this._socket.destroy();
    this._socket = null;
  }

  this.removeAllListeners();
};

